function fixXYZ() {
	we.min[0] = Math.min(we.x[0], we.x[1]);
	we.max[0] = Math.max(we.x[0], we.x[1]);
	we.min[1] = Math.min(we.y[0], we.y[1]);
	we.max[1] = Math.max(we.y[0], we.y[1]);
	we.min[2] = Math.min(we.z[0], we.z[1]);
	we.max[2] = Math.max(we.z[0], we.z[1]);
}

