var blockIds = [];
var blockDamages = [];
var lengths = [];

/*지형 복사*/

function WEcopy() {
	 fixXYZ();
  var n = 0, m = 0, o = 0;
  var nn = 0;
  blockIds = [];
  blockDamages = [];
  for(var xx = x1; xx < x2 + 1; xx++) {
    blockIds[n] = new Array(x2 - x1);
    blockDamages[n] = new Array(x2 - x1);
    for(var yy = y1; yy < y2 + 1; yy++) {
      blockIds[n][m] = new Array(y2 - y1);
      blockDamages[n][m] = new Array(y2 - y1);
      for(var zz = z1; zz < z2 + 1; zz++) {
        blockIds[n][m][o] = new Array();
        blockDamages[n][m][o] = new Array();
        blockIds[n][m][o] = getTile(xx, yy, zz);
        blockDamages[n][m][o] = Level.getData(xx, yy, zz);
        o++;
        nn++;
      }
      lengths[2] = o;
      o = 0;
      m++;
    }
    lengths[1] = m;
    m = 0;
    n++;
  }
  lengths[0] = n;
  Matin.toast(nn + "개의 블록들을 복사했습니다.");
}

/*지형 붙여넣기*/

function WEpaste() {
	 fixPosition();
  var nn = 0;
  for(var n = 0; n < lengths[0]; n++) {
    for(var m = 0; m < lengths[1]; m++) {
      for(var o = 0; o < lengths[2]; o++) {
        setTile(x1 + n, y1 + m, z1 + o, blockIds[n][m][o], blockDamages[n][m][o]);
        nn++;
      }
    }
  }
  Matin.toast(nn + "개의 블록들을 붙여넣었습니다.");
}

