function login(userId, password) {
    new java.lang.Thread({
        run: function() {
            try {
                var loginData = getLoginData().toString().split("\n");
                var pass = false;
                for (var n in loginData) {
                    var data = loginData[n].split("::");
                    if (data[0] == userId) {
                        if (data[1] == password) {
nicknameTitle = data[2];                                    
isLogined = true;
Matin.toast("["+nicknameTitle+"] "+ModPE.getName()+"님! 환영합니다!!");
                        } else {
   Matin.toast("아이디와 비밀번호가 일치하지 않습니다");
                        }
                        pass = true;
                        break;
                    }
                }
                if (!pass) Matin.toast("해당 아이디를 찾을 수 없습니다.\n회원가입 신청 후 로그인해 주세요.");
            } catch (e) {
                clientMessage(e + ", " + e.lineNumber);
            }
        }
    }).start();
}

