function procCmd(cmd) {
	if(cmd==퀴즈||퀴즈==true) {
		퀴z++;
		clientMessage("당신은 명령어를 너무 늦게 입력하셔서 이 이스터에그는 비활성화 되었습니다");
	}
if(cmd==퀴즈||퀴즈==true||퀴z==5) {
	clientMessage("\n이거 사실 사람 낚는 이스터에그임 ㅈㅅ");
}
var data = cmd.split(" ");
if(data[0]=="/set"&&we.we){
	fixXYZ();
	if(data.length==0){
		Matin.toast("//set 블럭아이디 블럭 데미지");
	}
	else{
		for(var x = we.min[0];x<=we.max[0];x++){
			for(var y = we.min[1];y<=we.max[1];y++){
				for(var z = we.min[2];z<=we.max[2];z++){
					Level.setTile(x, y, z, data[1], data[2]);
				}
			}
		}
	}
}
}

function fixXYZ() {
	we.min[0] = Math.min(we.x[0], we.x[1]);
	we.max[0] = Math.max(we.x[0], we.x[1]);
	we.min[1] = Math.min(we.y[0], we.y[1]);
	we.max[1] = Math.max(we.y[0], we.y[1]);
	we.min[2] = Math.min(we.z[0], we.z[1]);
	we.max[2] = Math.max(we.z[0], we.z[1]);
}

